            put.addColumn(Bytes.toBytes("info"), Bytes.toBytes("addr"), Bytes.toBytes("大连"));
            put.addColumn(Bytes.toBytes("info"), Bytes.toBytes("height"), Bytes.toBytes(RandomUtils.nextDouble(1.0, 2.0)));
